mapper.select:
SELECT ROWNUM, ID, POST_TITLE, POST_CONTENT, USER_ID, CREATE_DATE, UPDATE_DATE , USER_NAME, USER_SCHOOL, USER_DIVISION FROM ( SELECT ROWNUM R, ID, POST_TITLE, POST_CONTENT, USER_ID, CREATE_DATE, UPDATE_DATE , USER_NAME, USER_SCHOOL, USER_DIVISION FROM ( SELECT FP.ID, POST_TITLE, POST_CONTENT, USER_ID, FP.CREATE_DATE, FP.UPDATE_DATE , USER_NAME, USER_SCHOOL, USER_DIVISION FROM VIEW_FREE_POST FP JOIN TBL_USER U ON FP.USER_ID = U.ID
WHERE USER_ID = #{userId}
ORDER BY ID DESC ) P1
WHERE ROWNUM <= #{pagination.endRow} AND POST_TITLE LIKE '%' || #{keyword} || '%' ) P2 WHERE P2.R >= #{pagination.startRow}

SELECT ROWNUM, ID, POST_TITLE, POST_CONTENT, CREATE_DATE, UPDATE_DATE, USER_ID, USER_NAME, USER_SCHOOL, USER_DIVISION FROM ( SELECT ROWNUM R, ID, POST_TITLE, POST_CONTENT, CREATE_DATE, UPDATE_DATE, USER_ID, USER_NAME, USER_SCHOOL, USER_DIVISION FROM ( SELECT CP.ID, POST_TITLE, POST_CONTENT, CP.CREATE_DATE, CP.UPDATE_DATE, USER_ID , USER_NAME, USER_SCHOOL, USER_DIVISION FROM VIEW_CONSULTING_POST CP JOIN TBL_USER U ON CP.USER_ID = U.ID
WHERE USER_ID = #{userId}
ORDER BY ID DESC ) P1
WHERE ROWNUM <= #{pagination.endRow} AND POST_TITLE LIKE '%' || #{keyword} || '%' ) P2 WHERE P2.R >= #{pagination.startRow}

mapper.update:
UPDATE TBL_POST
SET POST_TITLE = #{postTitle}, POST_CONTENT = #{postContent} , UPDATE_DATE = CURRENT_TIMESTAMP
WHERE ID = #{id}

UPDATE TBL_REPLY
SET REPLY_CONTENT = #{replyContent}, UPDATE_DATE = CURRENT_TIMESTAMP
WHERE ID = #{id}
